<script>
$(function() {
	
	$(".helojob-logout").on('click', function(e) {
		e.preventDefault();
		$('#helojob-logout-modal').modal({
			backdrop: 'static',
			keyboard: false
		})
		.on('click', '#logout-confirmed', function(e) {
			window.location = "{{ URL::route('logout') }}";
		});
	});

	$('#logout-dismiss').click(function() {
		$('#helojob-logout-modal').modal('hide');
	});

	$(".swap-manguage").change(function(){
		var language = $(this).val();
		$.ajax({
			type: "POST",
			url: "{{ route('lang') }}",
			data: { lang : language},
			success: function(data){
				var parseData = JSON.parse(data);
				if(parseData.result == "OK"){
					window.location.reload();
				}
			}
		});
	});
});
</script>
